// Simple URL sanitization function (similar to WordPress esc_url)
function sanitizeUrl(url) {
  if (!url || typeof url !== 'string') {
    return '#';
  }

  // Trim whitespace
  url = url.trim();

  // If empty, return placeholder
  if (!url) {
    return '#';
  }

  // Allow common protocols
  const allowedProtocols = ['http:', 'https:', 'mailto:', 'tel:', 'ftp:'];
  try {
    // If it doesn't start with a protocol, assume https
    if (!url.match(/^[a-zA-Z][a-zA-Z0-9+.-]*:/)) {
      url = 'https://' + url;
    }
    const urlObj = new URL(url);

    // Check if protocol is allowed
    if (allowedProtocols.includes(urlObj.protocol)) {
      return urlObj.toString();
    }

    // Fallback for invalid protocols
    return '#';
  } catch (e) {
    // If URL is invalid, return placeholder
    return '#';
  }
}
